abrat <- read.table("out222.txt", header = TRUE, sep = ",")
head(abrat)

abrat$deltaXabs = abs(ratonera$deltaX)
tail(abrat)

abrat$deltaYabs = abs(ratonera$deltaY)
tail(abrat)

abratAbsx <- sum(abrat$deltaXabs)
abratAbsx 

abratAbsy <- sum(abrat$deltaYabs)
abratAbsy 

manhatan <- abratAbsx + abratAbsy
manhatan



